CREATE TABLE PUBLIC.TB_QUESTION_SUGGESTIONS(
	ID serial NOT NULL,
	IMG_SRC CHARACTER VARYING(100) NOT NULL,
	IMG_ALT CHARACTER VARYING(256) NOT NULL,
	SVG_IMAGE CHARACTER VARYING(16384),
	TITLE CHARACTER VARYING(256) NOT NULL,
	MAIN_TEXT CHARACTER VARYING(1024) NOT NULL,
	QUESTION_ID integer NOT NULL,
	PRIMARY KEY (ID),
	CONSTRAINT QUESTION_ID
		FOREIGN KEY (QUESTION_ID) REFERENCES PUBLIC.TB_QUESTION (ID) 
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE NO ACTION NOT VALID
);

CREATE TABLE PUBLIC.TB_SESSION_CONFIGURATION(
	ID serial NOT NULL,
	SESSION_ID CHARACTER VARYING(36) NOT NULL,
	CONFIG_KEY CHARACTER VARYING(36) NOT NULL,
	CONFIG_VALUE CHARACTER VARYING(255) NOT NULL,
	PRIMARY KEY (ID)
);

CREATE TABLE PUBLIC.TB_QUESTIONNAIRE_STATUS_SUGGESTIONS(
	ID serial NOT NULL,
	QUESTIONNAIRE_STATUS_ID INTEGER NOT NULL,
	MAIN_TEXT CHARACTER VARYING(2048) NOT NULL,
	PRIMARY KEY (ID),
	CONSTRAINT QUESTIONNAIRE_STATUS_ID
		FOREIGN KEY (QUESTIONNAIRE_STATUS_ID) REFERENCES PUBLIC.TB_QUESTIONNAIRE_STATUS (ID) 
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE
);

CREATE TABLE PUBLIC.TB_ONTOLOGY (
	ID serial NOT NULL,
	SESSION_ID CHARACTER VARYING(36) NOT NULL,
	RELATIONSHIPS JSONB NOT NULL,
	CREATED_AT TIMESTAMP DEFAULT NOW()
);

CREATE TABLE PUBLIC.TB_JWT_TOKEN(
	ID serial NOT NULL,
	EMAIL CHARACTER VARYING(256) NOT NULL,
	JWT_TOKEN CHARACTER VARYING(7168) NOT NULL,
	CREATED_AT TIMESTAMP DEFAULT NOW() NULL
);

CREATE TABLE PUBLIC.TB_GLOBAL_CONFIGURATION(
	ID serial NOT NULL,
	CONFIG_KEY CHARACTER VARYING(256) NOT NULL UNIQUE,
	CONFIG_VALUE CHARACTER VARYING(256) NOT NULL,
	DESCRIPTION CHARACTER VARYING(1024) NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Consultant

CREATE TABLE PUBLIC.TB_SKILL (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	SKILL_NAME CHARACTER VARYING(256) UNIQUE NOT NULL
);

CREATE TABLE PUBLIC.TB_CONSULTANT (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	GIVEN_NAME CHARACTER VARYING(256) NOT NULL,
	SURNAME CHARACTER VARYING(256) NOT NULL,
	EMAIL CHARACTER VARYING(256) UNIQUE NOT NULL,
	CV TEXT NOT NULL,
	CV_SUMMARY TEXT NULL,
	INDUSTRY_NAME CHARACTER VARYING(256) NULL,
	GEO_LOCATION CHARACTER VARYING(256) NULL,
	LINKEDIN_PROFILE_URL CHARACTER VARYING(1024) NULL,
	LINKEDIN_PHOTO_200 CHARACTER VARYING(1024) NULL,
	LINKEDIN_PHOTO_400 CHARACTER VARYING(1024) NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL,
	CONSTRAINT email_format CHECK (
        EMAIL ~* '^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$'
    )
);

CREATE TABLE PUBLIC.TB_CONSULTANT_IMAGE (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	CONSULTANT_ID INTEGER NOT NULL,
	CONSULTANT_IMAGE BYTEA NULL,
	IMAGE_NAME CHARACTER VARYING(256) NOT NULL,
	CONSTRAINT CONSULTANT_IMAGE_CONSULTANT_ID
		FOREIGN KEY (CONSULTANT_ID) REFERENCES PUBLIC.TB_CONSULTANT (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
	CONSTRAINT UNIQUE_CONSULTANT_IMAGE
        UNIQUE (CONSULTANT_ID)
);

CREATE TABLE PUBLIC.TB_CONSULTANT_SKILL (
    ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
    CONSULTANT_ID INTEGER NOT NULL,
    SKILL_ID INTEGER NOT NULL,
    CONSTRAINT CONSULTANT_SKILL_CONSULTANT_ID
        FOREIGN KEY (CONSULTANT_ID) REFERENCES PUBLIC.TB_CONSULTANT (ID)
        MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
    CONSTRAINT CONSULTANT_SKILL_SKILL_ID
        FOREIGN KEY (SKILL_ID) REFERENCES PUBLIC.TB_SKILL (ID)
        MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
    CONSTRAINT UNIQUE_CONSULTANT_SKILL
        UNIQUE (CONSULTANT_ID, SKILL_ID)
);

CREATE TABLE PUBLIC.TB_INDUSTRY (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	INDUSTRY_NAME CHARACTER VARYING(256) NOT NULL
);

CREATE TABLE PUBLIC.TB_COMPANY (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	COMPANY_NAME CHARACTER VARYING(256) UNIQUE NOT NULL
);

CREATE TABLE PUBLIC.TB_COMPANY_INDUSTRY (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	INDUSTRY_ID INTEGER NOT NULL,
	COMPANY_ID INTEGER NOT NULL,
	CONSTRAINT COMPANY_INDUSTRY_COMPANY_ID
		FOREIGN KEY (COMPANY_ID) REFERENCES PUBLIC.TB_COMPANY (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
	CONSTRAINT COMPANY_INDUSTRY_INDUSTRY_ID
		FOREIGN KEY (INDUSTRY_ID) REFERENCES PUBLIC.TB_INDUSTRY (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE
);

CREATE UNIQUE INDEX unique_email_ci ON PUBLIC.TB_CONSULTANT (LOWER(EMAIL));

CREATE TABLE PUBLIC.TB_CONSULTANT_EXPERIENCE (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	CONSULTANT_ID INTEGER NOT NULL,
	TITLE CHARACTER VARYING(256) NULL,
	LOCATION CHARACTER VARYING(256) NULL,
	START_DATE TIMESTAMP NOT NULL,
	END_DATE TIMESTAMP NULL,
	COMPANY_ID INTEGER NOT NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL,
	CONSTRAINT CONSULTANT_ID
		FOREIGN KEY (CONSULTANT_ID) REFERENCES PUBLIC.TB_CONSULTANT (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
	CONSTRAINT COMPANY_ID
		FOREIGN KEY (COMPANY_ID) REFERENCES PUBLIC.TB_COMPANY (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE
);

CREATE TABLE PUBLIC.TB_SESSION_CONSULTANT_RATING (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	CONSULTANT_ID INTEGER NOT NULL,
	SESSION_ID CHARACTER VARYING(36) NOT NULL,
	REASONING TEXT NOT NULL,
	RATING CHARACTER VARYING(20) NOT NULL,
	RATING_NUMBER SMALLINT NOT NULL CHECK (RATING_NUMBER BETWEEN 1 AND 5),
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL,
	CONSTRAINT CONSULTANT_ID
		FOREIGN KEY (CONSULTANT_ID) REFERENCES PUBLIC.TB_CONSULTANT (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE
);

-- Deep research output
CREATE TABLE PUBLIC.TB_DEEP_RESEARCH_OUTPUT (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	SESSION_ID CHARACTER VARYING(36) NOT NULL,
	ADVICE CHARACTER VARYING(8192) NOT NULL,
	OUTPUT TEXT NOT NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL
);

CREATE TABLE PUBLIC.TB_DEEP_RESEARCH_CITATION (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	DEEP_RESEARCH_OUTPUT_ID INTEGER NOT NULL,
	TITLE CHARACTER VARYING(256) NOT NULL,
	URL CHARACTER VARYING(2048) NOT NULL,
	START_INDEX INTEGER NOT NULL,
	END_INDEX INTEGER NOT NULL,
	TEXT TEXT NULL,
	CONSTRAINT DEEP_RESEARCH_OUTPUT_ID
		FOREIGN KEY (DEEP_RESEARCH_OUTPUT_ID) REFERENCES PUBLIC.TB_DEEP_RESEARCH_OUTPUT (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE
);

CREATE TABLE PUBLIC.TB_PROMPT_CATEGORY (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	NAME CHARACTER VARYING(256) NOT NULL,
	PROMPT_CATEGORY_PARENT_ID INTEGER NULL,
	LANGUAGE_ID INTEGER NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL,
	CONSTRAINT PROMPT_CATEGORY_PARENT_ID
		FOREIGN KEY (PROMPT_CATEGORY_PARENT_ID) REFERENCES PUBLIC.TB_PROMPT_CATEGORY (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
	CONSTRAINT UNIQUE_PROMPT_CATEGORY
        UNIQUE (NAME, PROMPT_CATEGORY_PARENT_ID, LANGUAGE_ID),
	CONSTRAINT LANGUAGE_ID
		FOREIGN KEY (LANGUAGE_ID) REFERENCES PUBLIC.TB_LANGUAGE (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE
);

CREATE TABLE PUBLIC.TB_PROMPT (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	PROMPT_CATEGORY_ID INTEGER NOT NULL,
	PROMPT_KEY CHARACTER VARYING(256) NOT NULL,
	PROMPT CHARACTER VARYING(65535) NOT NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL,
	CONSTRAINT PROMPT_CATEGORY_PROMPT_PROMPT_CATEGORY_ID
		FOREIGN KEY (PROMPT_CATEGORY_ID) REFERENCES PUBLIC.TB_PROMPT_CATEGORY (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
	CONSTRAINT UNIQUE_PROMPT_CATEGORY_PROMPT
        UNIQUE (PROMPT_CATEGORY_ID, PROMPT_KEY)
);

CREATE TABLE PUBLIC.TB_CONTEXT_DOCUMENTS (
	ID INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY,
	DOCUMENT_PATH CHARACTER VARYING(2048) NOT NULL,
	DOCUMENT_NAME CHARACTER VARYING(256) NOT NULL,
	COUNT INTEGER NOT NULL,
	DOCUMENT_EXTRACT TEXT NULL,
	QUESTIONNAIRE_STATUS_ID INTEGER NOT NULL,
	CREATED_AT TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
	UPDATED_AT TIMESTAMP NULL,
	CONSTRAINT CONQUESTIONNAIRE_STATUS_ID
		FOREIGN KEY (QUESTIONNAIRE_STATUS_ID) REFERENCES PUBLIC.TB_QUESTIONNAIRE_STATUS (ID)
		MATCH SIMPLE ON UPDATE NO ACTION ON DELETE CASCADE,
	CONSTRAINT UNIQUE_CONTEXT_DOCUMENTS
        UNIQUE (DOCUMENT_PATH, QUESTIONNAIRE_STATUS_ID)
);


